import React, { useState ,useEffect} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link, useNavigate, useParams } from 'react-router-dom';

const EditEmployee = () => {
        const[pkEmpId, setPkEmpId] = useState("")
    const[emailId, setEmailId] = useState("")
    const[passwd, setPasswd] = useState("")

    const navigate = useNavigate();
    const {id} = useParams

    const handlesubmit = (e)=>{
      e.preventDefault();
      axios.put(`http://localhost:8080/updateemployee/${id}`, 
                {pkEmpId:pkEmpId,
                    emailId:emailId,
                    passwd:passwd,
                })
                .then(()=>{
                  navigate("/retriveEmployee")
                });
    };

    function getdata(){
        axios.get(`http://localhost:8080/employee/${id}`)
        .then((res)=>{
            console.log(res.data);   //data is array added object
            setPkEmpId(res.data);
            setEmailId(res.data);
            setPasswd(res.data);
        });
    }

    useEffect(()=>{ // useEffect use to rerander only when change in getdata function
     getdata();
 },[]); 
  
  return (
    <div>
    <Form>
      <div className="mb-3">
        <div className="d-flex justify-content-between m-3">
        <h2>Edit Employee </h2>
        <Link to = "/RetriveEmployee">
        <Button variant="primary">Show data</Button>
        </Link>
        </div>
        <div>
        <label>pkEmpId</label>
        <input type="number" onChange={(e)=>setPkEmpId(e.target.value)}/>
        </div>
        <div>
        <label>EmailId</label>
        <input type="email" onChange={(e)=>setEmailId(e.target.value)}/>
        </div>
        <div>
        <label>Passwd</label>
        <input type="password" onChange={(e)=>setPasswd(e.target.value)} />
        </div>
        <Button type="submit" onClick={handlesubmit}>submit</Button>
        <Link to = "/RetriveEmployee">
        <Button variant="primary">Cancel</Button> 
        </Link>
      </div>
    </Form>
    </div>
  );
  
}

export default EditEmployee
