import React, { useState, useEffect } from "react";
import axios from 'axios'
import {Link, useNavigate } from "react-router-dom";

const PlaceOrder = () => {
 const navigator = useNavigate();

 const initialValue ={mobileNo: "", productName: "",couponCode: ""}
  const [order, setOrder] = useState(initialValue);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const { mobileNo, productName, couponCode } = order;
  const onInputChange = e => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const handleSubmit = async(e)=> {
    e.preventDefault();
    setFormErrors(validate(order));
    setIsSubmit(true);
    await axios.post("http://localhost:8080/placeorder", order)
    //alert(result.data);
    navigator("/");
  };

  
    useEffect(() => {
        console.log(formErrors);
        if(Object.keys(formErrors).length === 0 && isSubmit){
            console.log(formErrors);
        }
    }, [formErrors])

  const validate =(values)=>{
    const errors = {};
    
    if (!values.productName){
        errors.productName = "productName is required!";
    }
    if (!values.username){
      errors.username = "Username is required!";
  }
    if(!values.mobileNo){
        errors.mobileNo= "mobile No is required!";
    } else if(values.mobileNo=10){
        errors.password = "invalid mobile No";
    }
    return errors;
}



  return (
    <div className="container">
      <div className="w-75 mx-auto shadow p-5">
        <h2 className="text-center mb-4">Place Order</h2>
        <form onSubmit={e => handleSubmit(e)}>
        {Object.keys(formErrors).length === 0 && isSubmit ? <p>successful</p>: <p>unsuccessful</p>} 
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Mobile No"
              name="mobileNo"
              value={mobileNo}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Product Name"
              name="productName"
              value={productName}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Coupon Code"
              name="couponCode"
              value={couponCode}
              onChange={e => onInputChange(e)}
            />
          </div>
          <button className="btn btn-primary btn-block">submit</button>
          <Link to = "/">
          <button className="btn btn-primary btn-block">cancle</button></Link>
        </form>
      </div>
    </div>
  );
};

export default PlaceOrder;
