import React, { useState,useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
//import Form from 'react-bootstrap/Form';

const RetriveEmployee = () => {

  const{id} = useParams();
    const[employees, setEmployees] = useState([]);
  
    //save data in list
       const getEmployee=async()=>{
          const result = await axios.get('http://localhost:8080/getemployee')
               console.log(result.data);   //data is array added object
               setEmployees(result.data);
       };

       useEffect(()=>{ // useEffect use to rerander only when change in getdata function
        getEmployee();
    },[]); 
   
const deleteEmployee = async()=>{
   await axios.delete(`http://localhost:8080/deleteemployee/${id}`)
  getEmployee();

}


  return (
    <div>
         <h1>Read empoyee</h1>
          <div className="d-flex justify-content-between m-3">
         <Link to = "/createemployee">
         <Button variant="secondary">add Employee</Button>
         </Link>
         </div>
       <Table striped bordered hover>
       <thead>
         <tr>
           <th>id</th>
           <th>emailId </th>
           <th>passwd</th>
           <th>Action</th>
         </tr>
       </thead>
       <tbody >
       {
         employees.map((employee, index)=>{
             return(
             <tr>
              <td key = {index.employee.id}>{employee.id}</td>
             <td>{employee.emailId}</td>
             <td>{employee.passwd}</td>
             <td>
             <Button variant="primary">view</Button>
             {/* <Link to = {'/editemployee/${employee.id}'}>
                    <Button variant="secondary">Edit</Button>
             </Link> */}
             <Button variant="danger" onClick={()=>deleteEmployee(employee.id)}>Delete</Button>
             </td>
              </tr>
                )})
       }
       </tbody>
     </Table>
     </div>
  )
}

export default RetriveEmployee
