import React, { useState, useEffect } from "react";
import axios from 'axios'
import {Link } from "react-router-dom";
import Swal from 'sweetalert2';

const PlaceOrder = ({}) => {


 const initialValue ={mobileNo: "", productName: "",couponCode: ""}
  const [order, setOrder] = useState(initialValue);
  const [errors, setErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const { mobileNo, productName, couponCode } = order;
  const onInputChange = e => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const handleSubmit = async(e)=> {
    e.preventDefault();
    setErrors(validate(order));
    setIsSubmit(true);
    await axios.post("http://localhost:8080/placeorder", order)
  };
  
    useEffect(() => {
        console.log(errors);
        if(Object.keys(errors).length === 0 && isSubmit){
            console.log(errors);
        }
    }, [errors])

  const validate =(values)=>{
    const errors = {};
    
    if (!values.mobileNo){
        errors.mobileNo = "mobileNo is required!";
    }
    if(!values.productName){
      errors.productName = "productName is required!";
    }
    return errors;
}

const MySweetAlter = ()=>{
  Swal.fire({
      title: "Do you want to save the changes?",
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: "Save",
      denyButtonText: `Don't save`
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        Swal.fire("Saved!", "", "success");
      } else if (result.isDenied) {
        Swal.fire("Changes are not saved", "", "info");
      }
    });
  }


  return (
    <div className="con">
    <div className="containerout">
      <div className="container">
        <div>
        <h1 style={{color:"black"}}>Place Order</h1>
        <Link to = "/">
          <button className="btn btn-primary btn-block">cancle</button></Link></div>
        <form onSubmit={e => handleSubmit(e)}>
        {/* {Object.keys(errors).length === 0 && isSubmit ? <p>successful</p>: <p>unsuccessful</p>}  */}
          <div className="">
            <input
              type="text"
              placeholder="Enter Your Mobile No"
              name="mobileNo"
              value={mobileNo}
              onChange={e => onInputChange(e)}
            />
          </div>
          <p style={{color:"red"}}>{errors.mobileNo}</p>
          <div className="">
            <input
              type="text"
              placeholder="Enter Your Product Name"
              name="productName"
              value={productName}
              onChange={e => onInputChange(e)}
            />
          </div>
          <p style={{color:"red"}}>{errors.productName}</p>
          <div className="">
            <input
              type="text"
              placeholder="Enter Your Coupon Code"
              name="couponCode"
              value={couponCode}
              onChange={e => onInputChange(e)}
            />
          </div>
          <button className="btn btn-primary btn-block" onClick={MySweetAlter}>submit</button>
          
        </form>
      </div>
      </div>
    </div>
  );
};

export default PlaceOrder;
