import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";

const UpdateOrder = () => {
  let history = useHistory();
  // const { displayOrderId } = useParams();
  const [order, setOrder] = useState({
    displayOrderId: "",
    logisticName: "",
    awbNo: "",
  });

  const { displayOrderId, logisticName, awbNo} = order;
  const onInputChange = e => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    loadUser();
  }, []);

  const onSubmit = async e => {
    e.preventDefault();
    await axios.put(`http://localhost:3003/users/${displayOrderId}`, order);
    history.push("/");
  };

  const loadUser = async () => {
    const result = await axios.get(`http://localhost:3003/users/${displayOrderId}`);
    setOrder(result.data);
  };
  return (
    <div className="container">
      <div className="w-75 mx-auto shadow p-5">
        <h2 className="text-center mb-4">Edit Order</h2>
        <form onSubmit={e => onSubmit(e)}>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your displayOrderId"
              name="displayOrderId"
              value={displayOrderId}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your logistic Name"
              name="logisticName"
              value={logisticName}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              className="form-control form-control-lg"
              placeholder="Enter Your awb No"
              name="awbNo"
              value={awbNo}
              onChange={e => onInputChange(e)}
            />
          </div>
          <button className="btn btn-warning btn-block">Update Order</button>
        </form>
      </div>
    </div>
  );
};

export default UpdateOrder;
