import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link } from 'react-router-dom';


const modalContainer = {background:"white",
width: '400px',
 height: '400px',
position:"fixed", //important
top:"50%", 
left:"50%",
transform:"translate(-50%,-50%)",
padding: "3rem",
borderRadius:"0.5rem",
 maxWidth:"30rem",
}

// const mystyle = {
//     background: '#E0FFFF',
//     width: '400px',
//     height: '400px',
//     margin: '100px',
//     padding: '10px',
//     textAlign: 'center',
//     borderRadius: '20px',
//     boxShadow: '5px 5px 5px gray'
// }

const PlaceOrder = ({closeModal}) => {

    const [order, setOrder] = useState({
        mobileNo: "",
        productName: "",
        couponCode: "",
    })
    const { mobileNo, productName, couponCode } = order;

    const onInputChange = (e) => {
        setOrder({ ...order, [e.target.name]: e.target.value });
    };

    const sumitHandler = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:8080/placeorder', order)
    };

    return (
        <div style={{margin:"5rem"}} >
        <div style={{position:"fixed", left:0, right:0, bottom:0,top:'50px',background:"#A9A9A9"}} 
        onClick={closeModal}></div>
        <div style={modalContainer}>
                <h3>Place Order </h3>
                <Link to="/readorders">
                    <Button variant="primary">Show order </Button>
                </Link>
            </div>
            <Form onSubmit={(e) => sumitHandler(e)}>
                <Form.Group className="mb-3" >
                    <Form.Label>mobileNo</Form.Label>
                    <Form.Control type="text" placeholder="10-digit" name="mobileNo" value={mobileNo} onChange={(e) => onInputChange(e)} />
                </Form.Group>
                <Form.Group className="mb-3" >
                    <Form.Label>productName</Form.Label>
                    <Form.Control type="text" name="productName" value={productName} onChange={(e) => onInputChange(e)} />
                </Form.Group>
                <Form.Group className="mb-3" >
                    <Form.Label>couponCode</Form.Label>
                    <Form.Control type="text" name="couponCode" value={couponCode} onChange={(e) => onInputChange(e)} />
                </Form.Group>
                <label className="col-md-6 control-label"></label>
                <div className="col-md-10">
                    <Button type="submit"  >Send </Button>
                    <Button  variant="primary"onClick={closeModal}>Accept it</Button>
                </div>
            </Form>

        </div>
    )
}

export default PlaceOrder
