import React, { useState, useEffect } from "react";
import axios from 'axios';
import Form from 'react-bootstrap/Form';
import { NavLink } from "react-router-dom";


const modalContainer = {background:"white",
position:"fixed", //important
top:"50%", 
left:"50%",
transform:"translate(-50%,-50%)",
width: '500px',
 height: '800px',
borderRadius: '10px',
 boxShadow: 'rgba(0, 0, 0, 0.24) 0px 3px 8px' 

}

const CreateCustomer = ({closeModal}) => {
    const initialValue = { firstName: "", lastName: "", dob: "", gender: "", mobileNo: "", emailId: "", 
    address: "", district: "", state: "", country: "",pinCode:""}
    const [customer, setCustomer] = useState(initialValue);
    const [errors, setErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);
  
    const { firstName, lastName, dob, gender,mobileNo,emailId,address,district,state,country,pinCode} = customer;
    const onInputChange = e => {
        setCustomer({ ...customer, [e.target.name]: e.target.value });
    };
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      //setErrors(validate(order));
      setIsSubmit(true);
      await axios.post("http://localhost:8080/placeorder", customer)
    };
    // useEffect(() => {
    //   console.log(errors);
    //   if (Object.keys(errors).length === 0 && isSubmit) {
    //     console.log(errors);
    //   }
    // }, [errors]);
  
    // const validate = (values) => {
    //   const errors = {};
  
    //   if (values.mobileNo.length === 0) {
    //     errors.mobileNo = "mobileNo is required!";
    //   }
    //   if (values.productName.length === 0) {
    //     errors.productName = "productName is required!";
    //   }
    //   return errors;
    // }
  
  return (
    <div style={{margin:"100px"}} >
      <div style={modalContainer}>
        <Form >
          <div style={{ height: '50px', background: '#1873CC'}}><h2>Create Customer</h2></div>
          {Object.keys(errors).length === 0 && isSubmit ? <p  style={{textAlign:'center', color:'green', fontSize: '20px'}}>Your Order successfully Place</p> : <p style={{textAlign:'center'}}></p>}
          <div style={{ margin:"5px 30px 1px 30px"}}>
            <Form.Group className="mb-auto">
              <Form.Label>FirstName</Form.Label>
              <Form.Control type="text" placeholder="Enter Your first Name" name="firstName" value={firstName} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red", padding: '0' }}>{errors.firstName}</p>
           
            <Form.Group className="mb-auto">
              <Form.Label>Last_Name</Form.Label>
              <Form.Control type="text" placeholder="Enter Your last Name" name="lastName" value={lastName} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.lastName}</p>
           
            <Form.Group className="mb-auto" >
              <Form.Label>Gender</Form.Label>
              <Form.Control type="text" placeholder="Enter Your gender" name="gender" value={gender} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.lastName}</p>
            <Form.Group className="mb-auto" >
              <Form.Label>Mobile_No</Form.Label>
              <Form.Control type="text" placeholder="Enter Your mobileNo" name="mobileNo" value={mobileNo} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.mobileNo}</p>

            <Form.Group className="mb-auto" >
              <Form.Label>Email_Id</Form.Label>
              <Form.Control type="text" placeholder="Enter Your emailId" name="emailId" value={emailId} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.emailId}</p>

            <Form.Group className="mb-auto" >
              <Form.Label>Address</Form.Label>
              <Form.Control type="text" placeholder="Enter Your address" name="address" value={address} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.address}</p>

            <Form.Group className="mb-auto" >
              <Form.Label>District</Form.Label>
              <Form.Control type="text" placeholder="Enter Your district" name="district" value={district} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.district}</p>

            <Form.Group className="mb-auto" >
              <Form.Label>State</Form.Label>
              <Form.Control type="text" placeholder="Enter Your state" name="state" value={state} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.state}</p>

            <Form.Group className="mb-auto" >
              <Form.Label>Country</Form.Label>
              <Form.Control type="text" placeholder="Enter Your country" name="country" value={country} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.country}</p>
            <Form.Group className="mb-auto" >
              <Form.Label>Pin_Code</Form.Label>
              <Form.Control type="number" placeholder="Enter Your pinCode" name="pinCode" value={pinCode} onChange={e => onInputChange(e)} />
            </Form.Group>
            <p style={{ color: "red"}}>{errors.pinCode}</p>
          </div>
          <div>
          <button className="btn btn-primary" style={{background:'#1873CC',marginLeft:'70px',marginTop:'30px'}}>Place Order</button>
          <NavLink className="btn btn-primary mr-2" to="/" style={{ background: 'red', marginLeft: '30px', marginTop: '30px' }} >Cancle</NavLink>
          </div> 
        </Form>
      </div>
    </div>
  );
}
export default CreateCustomer;
